import java.io.*;public class SistemaEscolar{	private Escola e1;	private BufferedReader reader;		public static void main(String[] args){		try{			SistemaEscolar se = new SistemaEscolar();			se.e1 = new Escola();			se.reader = new BufferedReader(new InputStreamReader(System.in));			System.out.println("informe o Nome da Escola:");			se.e1.setNome(se.reader.readLine());			System.out.println("Informe o Telefone da Escola:");			se.e1.setFone(se.reader.readLine());						se.menu();		}catch(Exception e1){			System.out.println("Deu erro");		}	}			public void menu(){			try{				String opcao = "";				while(!opcao.equals("4")){					System.out.println("\n------------------------------");					System.out.println("[1] - Cadastrar nova turma");					System.out.println("[2] - Listar turmas existentes");					System.out.println("[3] - Consultar turma");					System.out.println("[4] - Sair");					opcao = this.reader.readLine();										if (opcao.equals("1")){						this.cadastrarTurma();					}else if(opcao.equals("2")){						this.listarTurmas();					}else if(opcao.equals("3")){						this.consultarTurma();					}else if(opcao.equals("4")){						System.out.println("Encerrando...");					}				}			}catch(IOException e1){;				System.out.println("Um erro ocorreu...");			}			}				public void cadastrarTurma(){		try{			Turma turma = new Turma();			System.out.println("Informe o numero da turma:");			String numTurma = this.reader.readLine();			turma.setNumeroTurma(Integer.parseInt(numTurma));						System.out.println("Informe o nome do curso da turma:");			turma.setNome_curso(this.reader.readLine());						System.out.println("Informe o ano da turma:");			String ano = this.reader.readLine();			turma.setAno(Integer.parseInt(ano));						for (int i = 0; i < 40; i++){				System.out.println("\n-----------------------------------");				System.out.println("Informe o nome do aluno:");				String nomeAluno = this.reader.readLine();				if(nomeAluno.equals("")){					break;				}				Aluno aluno = new Aluno();				aluno.setNome(nomeAluno);								System.out.println("Informe a matricula:");				aluno.setMatricula(this.reader.readLine());								System.out.println("Informe a nota 1:");				String n1 = this.reader.readLine();				aluno.setNota1(Float.parseFloat(n1));								System.out.println("Informe a nota 2:");				String n2 = this.reader.readLine();				aluno.setNota2(Float.parseFloat(n2));								System.out.println("Informe a nota 3:");				String n3 = this.reader.readLine();				aluno.setNota3(Float.parseFloat(n3));								System.out.println("Informe a nota 4:");				String n4 = this.reader.readLine();				aluno.setNota4(Float.parseFloat(n4));								turma.setAluno(i,aluno);			}						this.e1.setQtdeTurmas(this.e1.getQtdeTurmas()+1);			this.e1.setTurma(turma);		}catch(Exception e1){			System.out.println("Ocorreu um erro ao cadastrar a turma...");		}						}		public void listarTurmas(){		System.out.println("\n\nRelatorio de Turmas:\n");		for(int i = 0; i < this.e1.getQtdeTurmas(); i++){			String linha = "Num:" + this.e1.getTurma(i).getNumeroTurma();			System.out.print(linha);			linha = " - Curso:" + this.e1.getTurma(i).getNome_curso();			System.out.print(linha);			linha = " - Ano:" + this.e1.getTurma(i).getAno();			System.out.print(linha+"\n");					}	}		public void consultarTurma(){		try{			System.out.println("\n\nConsulta de Turma\n");			System.out.println("Informe o numero da turma:");			String nTurma = this.reader.readLine();			int numTurma = Integer.parseInt(nTurma);			boolean achou = false;			for(int i = 0; i < this.e1.getQtdeTurmas(); i++){				Turma t = this.e1.getTurma(i);				if(t.getNumeroTurma() == numTurma){					System.out.println("\nAlunos:\n");					int pos = 0 ;					while(t.getAluno(pos) != null){						String linha = "nome: " + t.getAluno(pos).getNome();						System.out.print(linha);						linha = " - Media: " + t.getAluno(pos).Calcular_media() + "\n";						System.out.print(linha);						pos++;					}					achou = true;					break;				}			}			if(!achou){				System.out.println("\nTurma nao encontrada\n");			}		}catch(Exception e1){			System.out.println("Ume erro obscuro ocorreu: " + e1.getMessage());				}	}}